import { createSlice, configureStore } from "@reduxjs/toolkit";
import { composeWithDevTools } from "redux-devtools-extension";

const initialState1 = { counter: 0, showCounter: true };
const initialState2 = {counter: 5,}

// can mutate the state unlike using reducer function
const counterSlice1 = createSlice({
  name: "counter",
  initialState1,
  reducers: {
    // receive current state and action as first and second parameter
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter = state.counter + action.payload;
    },
    toggleCounter(state) {
      state.showCounter = !state.showCounter;
    },
  },
});

//store
const store = configureStore({
  reducer: counterSlice1.reducer,
  // Apply Redux DevTools extension
});

//contains generated action creators for each reducer action
export const counterActions = counterSlice1.actions;
export default store;
